package org.jetbrains.letsPlot.visualtesting.plot

import io.ktor.http.*
import io.ktor.server.application.*
import io.ktor.server.cio.*
import io.ktor.server.engine.*
import io.ktor.server.response.*
import io.ktor.server.routing.*
import kotlinx.coroutines.runBlocking
import org.jetbrains.letsPlot.commons.encoding.Base64

class StubRasterTileServer {
    private var server: EmbeddedServer<CIOApplicationEngine, CIOApplicationEngine.Configuration>? = null

    fun start(): Int {
        val serverInstance = embeddedServer(CIO, port = 0) {
            setupRouting()
        }
        serverInstance.start(wait = false)

        // Get the resolved random port
        val port = runBlocking { serverInstance.engine.resolvedConnectors().first().port }
        server = serverInstance
        return port
    }

    fun stop() {
        server?.stop(0, 0)
        server = null
    }

    private fun Application.setupRouting() {
        routing {
            get("/{z}/{y}/{x}/{ext}") {
                val z = call.parameters["z"]
                val y = call.parameters["y"]
                val x = call.parameters["x"]
                val ext = call.parameters["ext"]

                val tile = tiles["$z/$y/$x/$ext"]

                if (tile == null) {
                    call.respond(HttpStatusCode.NotFound, "Tile not found: ${call.parameters}")
                    return@get
                }

                call.respondBytes(tile, ContentType.Image.PNG)
            }
        }
    }

    companion object {
        val tile0png = Base64.decode("")
        val tile1png = Base64.decode("")
        val tile2png = Base64.decode("")
        val tile3png = Base64.decode("")

        val tile0jpg = Base64.decode("""""")
        val tile1jpg = Base64.decode("""""")
        val tile2jpg = Base64.decode("""""")
        val tile3jpg = Base64.decode("""""")

        val tiles = mapOf(
            "1/0/0/png" to tile0png,
            "1/0/1/png" to tile1png,
            "1/1/0/png" to tile2png,
            "1/1/1/png" to tile3png,

            "1/0/0/jpg" to tile0jpg,
            "1/0/1/jpg" to tile1jpg,
            "1/1/0/jpg" to tile2jpg,
            "1/1/1/jpg" to tile3jpg,
        )
    }
}

fun runStubRasterTileServer(ext: String, block: (url: String) -> Unit) {
    val server = StubRasterTileServer()
    val port = server.start()
    val url = "http://localhost:$port/{z}/{y}/{x}/$ext"

    try {
        runBlocking {
            block(url)
        }
    } finally {
        server.stop()
    }
}